import React, { useState, useEffect } from 'react';
import IngredientList from './components/IngredientList';
import SelectedIngredients from './components/SelectedIngredients';
import RecipeList from './components/RecipeList';
import './App.css';

function App() {
    const [ingredients, setIngredients] = useState([]);
    const [selectedIngredients, setSelectedIngredients] = useState([]);
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        // Fetch predefined ingredients from local storage or API
        setIngredients(['Tomato', 'Cheese', 'Basil', 'Chicken', 'Beef', 'Onion', 'Garlic']);
    }, []);

    const handleIngredientSelect = (ingredient) => {
        setSelectedIngredients(prev => [...prev, ingredient]);
    };

    const handleSearchRecipes = async () => {
        const response = await fetch('http://localhost:5000/api/recipes/find', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ ingredients: selectedIngredients }),
        });
        const data = await response.json();
        setRecipes(data);
    };

    return (
        <div className="App">
            <h1>Recipe Finder</h1>
            <IngredientList ingredients={ingredients} onSelect={handleIngredientSelect} />
            <SelectedIngredients ingredients={selectedIngredients} onSearch={handleSearchRecipes} />
            <RecipeList recipes={recipes} />
        </div>
    );
}

export default App;
